import { Component, OnInit } from '@angular/core';
import { ScoreService } from '../../services/score.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { GameStateService } from '../../services/game-state.service';
import { SnackBarMessageComponent } from '../snack-bar-message/snack-bar-message.component';
import { Score } from '../../models/score';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {

  _score: Score = {score: [0, 0]};

  constructor(private scoreService: ScoreService,
              private gameStateService: GameStateService,
              private snackBar: MatSnackBar) {}

  ngOnInit() {
    this.scoreService.teamScored().subscribe(teamId => {
      this._score.score[teamId] = this._score.score[teamId] + 1;
      this.showScoreToast();
    });

    this.gameStateService.gameOver().subscribe(winner => {
      let message: string;

      if (winner.winners.length > 1) {
        message = 'It\'s a draw';
      } else {
        message = 'Team ' + winner.winners[0] + ' won the game';
      }
      this.resetScore();
      this.showToast(message, 15000);
    });
  }

  private resetScore(): void {
    this._score = {score: [0, 0]};
  }

  private showScoreToast() {
    if (!this.isZeroZero(this._score)) {
      this.showToast('GOOOOAL! Score is now' + ' ' + this._score.score[0] + ' - ' + this._score.score[1], 5000);
    }
  }

  private showToast(message: string, duration: number): void {
    this.snackBar.openFromComponent(
      SnackBarMessageComponent,
      {duration: duration, data: message}
    );
  }

  private isZeroZero(score: Score): boolean {
    return (score.score[0] === 0) && (score.score[1] === 0);
  }
}

import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';
import { MqttService } from 'ngx-mqtt';
import { Velocity } from '../models/velocity';
import { switchMap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class VelocityService {

  constructor(private mqttService: MqttService) { }

  velocity(): Observable<Velocity> {
    return this.mqttService.observe('ball/velocity/kmh').pipe(switchMap(message => {

      const payload = message.payload.toString();
      const velocity: Velocity = {velocity: Number(payload)};
      const roundedVelocity = Math.round(velocity.velocity);
      return of({velocity: roundedVelocity});
    }));
  }

  mockVelocityFromMqtt(velocity: Velocity): Observable<void> {
    return this.mqttService.publish('ball/velocity/kmh', velocity.velocity.toString());
  }
}

import { Component, OnInit } from '@angular/core';
import { VelocityService } from '../../services/velocity.service';
import { Velocity } from '../../models/velocity';
import { GameStateService } from '../../services/game-state.service';

@Component({
  selector: 'app-velocity',
  templateUrl: './velocity.component.html',
  styleUrls: ['./velocity.component.scss']
})
export class VelocityComponent implements OnInit {
  _velocity: Velocity = {velocity: 0};
  gaugeType = 'arch';
  gaugeLabel = 'Geschwindigkeit';
  gaugeAppendText = 'km/h';
  thresholdConfig = {
    '0': {color: 'green'},
    '40': {color: 'orange'},
    '75.5': {color: 'red'}
  };

  constructor(private velocityService: VelocityService, private gameStateService: GameStateService) { }

  ngOnInit() {
    this.gameStateService.gameStart().subscribe(message => this._velocity = {velocity: 0});

      this.velocityService.velocity().subscribe(velocity => {
          this._velocity = velocity;
      });
  }
}

import { Component } from '@angular/core';
import { ScoreService } from '../../services/score.service';
import { Score } from '../../models/score';
import { GameStateService } from '../../services/game-state.service';

@Component({
  selector: 'app-score',
  templateUrl: './score.component.html',
  styleUrls: ['./score.component.scss']
})
export class ScoreComponent {
  _score: Score = {score: [0, 0]};

  constructor(private scoreService: ScoreService, private gameStateService: GameStateService) {
    scoreService.score(0).subscribe(score => {
      this._score.score[0] = score;
    });

    scoreService.score(1).subscribe(score => {
      this._score.score[1] = score;
    });
  }

  resetGame(): void {
    this.gameStateService.resetGame().subscribe(() => {});
  }
}

import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';
import { Position } from '../models/position';
import { MqttService } from 'ngx-mqtt';
import { switchMap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class HeatmapService {

  constructor(private mqttService: MqttService) { }

  heatmapData(): Observable<Position> {
    return this.mqttService.observe('ball/position/rel').pipe(switchMap(message => {
      const payload = message.payload.toString();
      const values = payload.split(',');
      const roundedXValue = this.roundToTwoFractionDigits(Number(values[0]));
      const roundedYValue = this.roundToTwoFractionDigits(Number(values[1]));
      return of({x: roundedXValue, y: roundedYValue});
    }));
  }

  mockHeatmapDataFromMqtt(position: Position): Observable<void> {
    return this.mqttService.publish('ball/position/rel', position.x.toString() + ',' + position.y.toString());
  }

  private roundToTwoFractionDigits(value: number): number {
    return Math.round(value * 100) / 100;
  }
}

